import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function Login({ setModalBox }) {
  const [error, setError] = useState(null);
  const history = useHistory();

  const handleLogin = async () => {
    const login = document.getElementById('login').value;
    const password = document.getElementById('password').value;
    const data = { login, password };

    try {
      const response = await fetch('http://localhost:9001/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Неправильный логин или пароль');
      }

      const userData = await response.json(); // Получаем все данные пользователя, включая токен
      localStorage.setItem('userData', JSON.stringify(userData)); // Сохраняем все данные пользователя в localStorage

      // Закрываем модальное окно после успешного входа
      setModalBox('none');

      // Перенаправляем пользователя на личный кабинет
      history.push('/personal-account');
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <>
      <h1>Логин</h1>
      <input type='text' id='login' placeholder='Логин' />
      <input type='password' id='password' placeholder='Пароль' />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <button onClick={handleLogin}>Войти</button>
    </>
  );
}

export default Login;
